mapper.select:
select id, car_num carNum, brand, guide_price guidePrice, produce_time produceTime, car_type carType
from t_car
where car_num = #{carNum};

mapper.update:
update t_car
set brand=#{brand}
  ,guide_price= #{guidePrice}
  ,produce_time= #{produceTime}
  , car_type=#{carType}
where car_num = #{carNum};
